package com.class17;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
EvenOrOdd obj=new EvenOrOdd();
obj.EvenOdd(5);
	}
void EvenOdd(int a) {
	if(a%2==0) {
		System.out.println(a+" Even number");
	}else {
		System.out.println(a+" Odd number");
	}
}
}
