package com.class2;

public class PerimeterArea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int width, height;
  width=5;
  height=8;
  
  int perimeter, area;
  perimeter=(width+height)*2;
  area=width*height;
  System.out.println("The perimeter of rectangle with width 5 and height 8 is equal to "+perimeter+" and the area is "+area);
  
	}

}
