package com.reviewloops;

public class Review {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1; i<=10; i++) {
			System.out.println("I am feeling happy today"+i);
		}
		int num=1;
		while(num<=10)
		{
			System.out.println("I am feeling happy today"+num);
			num++;
			
		}
		for(int a=18; a>=0; a-=3) {
			System.out.println(a);
			
		}
		int n=0;
		
		do {
			
			System.out.println(n);
			n++;
		}
		while(n<=10);
	
	}

}
